	public static final String PASSWORD = ALPHABET + ALPHABET_CAPS + SYMBOLS + NUMBERS;
	public static final String USERNAME = ALPHABET + ALPHABET_CAPS + NUMBERS + "_";
	
	public static final int MAX_EMAIL_LENGTH = 320;
